export function removeMarks(node) {
  const newNode = {
    ...node
  };
  delete newNode.marks;
  return newNode;
}
export function removeNonAnnotationMarks(node) {
  const newNode = {
    ...node
  };
  if (node.marks) {
    newNode.marks = node.marks.filter(mark => mark.type === 'annotation');
  }
  return newNode;
}