import { scrollGutterPluginKey } from '../pm-plugins/scroll-gutter/plugin-key';
export const setKeyboardHeight = keyboardHeight => (state, dispatch) => {
  const {
    tr
  } = state;
  tr.setMeta(scrollGutterPluginKey, {
    keyboardHeight
  });
  if (dispatch) {
    dispatch(tr);
  }
  return true;
};