import { interactionTrackingPluginKey } from './pm-plugin';
export const stopEditing = view => {
  view.dispatch(view.state.tr.setMeta(interactionTrackingPluginKey, {
    type: 'stopEditing'
  }));
};
export const startEditing = view => {
  view.dispatch(view.state.tr.setMeta(interactionTrackingPluginKey, {
    type: 'startEditing'
  }));
};