// find node in descendants by condition
export function findNode(parent, predicate) {
  let matchedNode;
  parent.descendants(node => {
    // dont run predicate if node already found
    if (matchedNode) {
      return false;
    }
    if (predicate(node)) {
      matchedNode = node;
      return false;
    }
    return true;
  });
  return matchedNode;
}