export default ((state, action) => {
  switch (action.type) {
    case 'openPixelEditor':
      {
        return {
          ...state,
          isPixelEditorOpen: true
        };
      }
    case 'closePixelEditor':
      {
        return {
          ...state,
          isPixelEditorOpen: false
        };
      }
    default:
      return state;
  }
});