import { removeMark } from '@atlaskit/editor-common/mark';
import { ACTIONS, pluginKey } from '../main';
export const removeColor = () => (state, dispatch) => {
  const {
    schema
  } = state;
  const {
    textColor
  } = schema.marks;
  const tr = state.tr;
  removeMark(textColor)({
    tr
  });
  if (dispatch) {
    dispatch(tr.setMeta(pluginKey, {
      action: ACTIONS.RESET_COLOR
    }));
  }
  return true;
};