import pick from 'lodash/pick';
import { printAstToDoc } from './print-ast-to-doc';
import { printDocToString } from './print-doc-to-string';
/**
 * Print the provided AST object into a formatted JQL string.
 */
export const print = (jast, options = {}) => {
  return printDocToString(printAstToDoc(jast, pick(options, ['operatorCase'])), pick(options, ['printWidth']));
};