import { JQLParser } from '@atlaskit/jql-parser';
import { operators } from './constants';
export const isPredicateOperand = ruleStack => {
  return ruleStack.includes(JQLParser.RULE_jqlPredicateOperand);
};
export const getPositionFromParserRule = ctx => [ctx.start.startIndex, ctx.stop ? ctx.stop.stopIndex + 1 : ctx.start.stopIndex + 1];
export const normalizeText = text => text.toLowerCase().replace(/\s+/g, ' ').trim();
export const isOperator = maybeOperator => operators.includes(maybeOperator);