import { useCallback } from 'react';
import { useAnalyticsEvents } from '@atlaskit/analytics-next';
export const useJqlPackageAnalytics = (analyticsSource, packageName, packageVersion, analyticsChannel) => {
  const {
    createAnalyticsEvent
  } = useAnalyticsEvents();
  const createAndFireAnalyticsEvent = useCallback(payload => {
    const event = createAnalyticsEvent({
      ...payload,
      attributes: {
        ...payload.attributes,
        analyticsSource,
        packageName,
        packageVersion
      }
    });
    event.fire(analyticsChannel);
  }, [createAnalyticsEvent, analyticsSource, packageName, packageVersion, analyticsChannel]);
  return {
    createAndFireAnalyticsEvent
  };
};