import { useEffect } from 'react';
import { failUfoExperience } from '../index';
export const useColumnPickerRenderedFailedUfoExperience = (status, experienceId) => {
  useEffect(() => {
    if (status === 'rejected' && experienceId) {
      failUfoExperience({
        name: 'column-picker-rendered'
      }, experienceId);
    }
  }, [experienceId, status]);
};