import { useReducer } from 'react';
export const ACTION_CLEAR = 'CLEAR';
export const ACTION_LOADING = 'LOADING';
export const ACTION_SUCCESS = 'SUCCESS';
export const ACTION_ERRORED = 'ERROR';
export const INITIAL_STATE = {
  items: null,
  error: null,
  isLoading: false
};
export function reducer(state, action) {
  switch (action.type) {
    case ACTION_CLEAR:
      return {
        ...INITIAL_STATE
      };
    case ACTION_LOADING:
      return {
        ...INITIAL_STATE,
        isLoading: true
      };
    case ACTION_SUCCESS:
      return {
        ...state,
        ...action.payload,
        error: null
      };
    case ACTION_ERRORED:
      return {
        ...INITIAL_STATE,
        error: action.payload
      };
    default:
      return state;
  }
}
export const usePluginReducer = () => useReducer(reducer, INITIAL_STATE);