import { useAvailableSites } from '../useAvailableSites';
export const useCloudIdToUrl = (cloudId, gatewayBaseUrl) => {
  const {
    data,
    loading,
    error
  } = useAvailableSites({
    gatewayBaseUrl
  });
  const filterData = data.filter(site => site.cloudId === cloudId);
  if (filterData.length > 0) {
    return {
      data: filterData[0].url,
      loading,
      error
    };
  }
  return {
    data: undefined,
    loading,
    error
  };
};