export const isFinalCardStatus = status => ['complete', 'error', 'failed-processing'].includes(status);
export const getCardStatus = (fileStatus, {
  isPreviewable,
  hasPreview
}) => {
  switch (fileStatus) {
    case 'uploading':
    case 'failed-processing':
    case 'error':
    case 'processing':
      return fileStatus;
    case 'processed':
      if (!isPreviewable || !hasPreview) {
        return 'complete';
      }
      return 'loading-preview';
    default:
      return 'loading';
  }
};