import { MediaClient } from '@atlaskit/media-client';
import { mediaClientsMap } from '../MediaClientProvider';
export const mockedGetMediaClient = (mediaClientConfig, mediaStore) => {
  if (!mediaClientConfig) {
    return new MediaClient({
      authProvider: () => Promise.resolve({
        clientId: '',
        token: '',
        baseUrl: ''
      })
    }, undefined, mediaStore);
  }
  let mediaClient = mediaClientsMap.get(mediaClientConfig);
  if (!mediaClient) {
    mediaClient = new MediaClient(mediaClientConfig, undefined, mediaStore);
    mediaClientsMap.set(mediaClientConfig, mediaClient);
  }
  return mediaClient;
};