import { getDimensionsFromBlob } from '@atlaskit/media-client';
export const isUnknownDimensions = dimensions => !dimensions.width && !dimensions.height;
export async function getPreviewFromBlob(mediaType, file) {
  switch (mediaType) {
    case 'image':
    case 'video':
      {
        const dimensions = await getDimensionsFromBlob(mediaType, file);
        if (isUnknownDimensions(dimensions)) {
          return {
            file,
            scaleFactor: 1
          };
        }
        return {
          file,
          dimensions,
          scaleFactor: 1
        };
      }
    default:
      return {
        file
      };
  }
}