import { getFileAttributes } from '../..';
export const createPreviewUnsupportedEvent = fileState => {
  const {
    fileId,
    fileMediatype,
    fileMimetype,
    fileSize
  } = getFileAttributes(fileState);
  return {
    eventType: 'operational',
    actionSubject: 'mediaFile',
    action: 'previewUnsupported',
    attributes: {
      fileAttributes: {
        fileId,
        fileMediatype,
        fileMimetype,
        fileSize
      }
    }
  };
};