import { getFileAttributes } from '../..';
export const createDownloadButtonClickedEvent = fileState => {
  const {
    fileId,
    fileMediatype,
    fileMimetype,
    fileSize
  } = getFileAttributes(fileState);
  return {
    eventType: 'ui',
    action: 'clicked',
    actionSubject: 'button',
    actionSubjectId: 'downloadButton',
    attributes: {
      fileAttributes: {
        fileId,
        fileMediatype,
        fileMimetype,
        fileSize
      },
      fileProcessingStatus: fileState.status
    }
  };
};