import { isErrorFileState } from '@atlaskit/media-client';
export const getObjectUrlFromFileState = async state => {
  if (!isErrorFileState(state)) {
    const {
      preview
    } = state;
    if (preview) {
      try {
        // @ts-expect-error
        return URL.createObjectURL((await preview).value);
      } catch (err) {
        return undefined;
      }
    }
  }
  return undefined;
};