import React, { type ReactNode } from 'react';
/**
 * Annotate part of the react tree with a product name
 * ```
 * <UFOLabel name="card">
 *   <Card card={data} />
 * </UFOLabel>
 * ```
 */
export default function UFOLabel({ name, children }: {
    name: string;
    children: ReactNode;
}): React.JSX.Element;
